$(document).ready(function() {
	var winCount = 0;
	var lossCount = 0;

	//starting or restarting the game with the click on the start button
	$('#start-game').on('click', function() {

		//every gem is having their initial scores set to zero
		var diamondScore = 0;
		var emeraldScore = 0;
		var rubyScore = 0;
		var sapphireScore = 0;

		//everytime the game starts, random score generated by the computer and user's score are set to zero
		var randomScore = 0;
		var scoreCounter = 0;

		$('#your-score').text(scoreCounter);
		$('#wins').text(winCount);
		$('#loss').text(lossCount);

		//displays a message about the result at the very end of the game and sets it to empty when the game starts
		$('#message').empty();

		//any number between 19 and 120
		randomScore = Math.floor(Math.random() * 120 + 19);
		$('#targeted-score').text(randomScore);

		//all gems scores are between 1 and 12
		diamondScore = Math.floor(Math.random() * 12 + 1);
		console.log(diamondScore);
		var diamond = $('#diamond');
		//setting an attribute to the gem's score
		diamond.attr('data-crystalValue', diamondScore);

		emeraldScore = Math.floor(Math.random() * 12 + 1);
		console.log(emeraldScore);
		var emerald = $('#emerald');
		emerald.attr('data-crystalValue', emeraldScore);

		rubyScore = Math.floor(Math.random() * 12 + 1);
		console.log(rubyScore);
		var ruby = $('#ruby');
		ruby.attr('data-crystalValue', rubyScore);

		sapphireScore = Math.floor(Math.random() * 12 + 1);
		console.log(sapphireScore);
		var sapphire = $('#sapphire');
		sapphire.attr('data-crystalValue', sapphireScore);

		//upon clicking on the every gem, their values are retrieved and stored in crystalValue variable
		$('.crystal-image').on('click', function() {
			console.log('score counter:', scoreCounter);
			console.log('randomScore', randomScore);
			var crystalValue = $(this).attr('data-crystalValue');
			crystalValue = parseInt(crystalValue);
			scoreCounter += crystalValue;
			$('#your-score').text(scoreCounter);

			if (scoreCounter === randomScore) {
				$('#message').html( //displays a message at the very bottom of the page
					"<div class = 'alert alert-success'>You won the game! Please press the Start button to play again.</div>"
				);
				winCount++;
				$('#wins').text(winCount);
				//resets these scores to zero
				scoreCounter = 0;
				randomScore = 0;
				crystalValue = 0;
			} else if (scoreCounter > randomScore) {
				$('#message').html(
					"<div class = 'alert alert-danger'>You lost the game! Please press the Start button to play again.</div>"
				);
				lossCount++;
				$('#loss').text(lossCount);
				//resets these scores to zero
				scoreCounter = 0;
				randomScore = 0;
				crystalValue = 0;
			}
		});
	});
});
